function ocultaElementosDoHeader(){document.querySelector("header").classList.add("header--is-hidden")}function exibeElementosDoHeader(){document.querySelector("header").classList.remove("header--is-hidden")}function botaoVerMenos(){document.querySelector("[data-button-list]").innerHTML="Ver menos"}function botaoVerMais(){document.querySelector("[data-button-list]").innerHTML="Ver mais"}function abreOuFechaResposta(e){e.target.parentNode.classList.toggle("perguntas__lista__item--is-open")}document.addEventListener("DOMContentLoaded",function(){var e=document.querySelector("[data-button-list]"),t=document.querySelectorAll("[data-pergunta-frequente]");const o=document.querySelector(".hero").clientHeight;window.addEventListener("scroll",function(){(window.scrollY<o?ocultaElementosDoHeader:exibeElementosDoHeader)()}),e.addEventListener("click",function(e){e=e.target.dataset.buttonList,e=document.querySelector(`[data-target-list=${e}]`);(1==e.classList.contains("painel__lista--is-closed")?(e.classList.remove("painel__lista--is-closed"),botaoVerMenos):(e.classList.add("painel__lista--is-closed"),botaoVerMais))()});for(let e=0;e<t.length;e++)t[e].addEventListener("click",abreOuFechaResposta)});